function sendData(o,a,i,t){$.post("/forum/topic/"+o+"/"+a).success(function(){$("label[for='"+a+"']").toggleClass(i+" "+t)})}$("#follow").on("click",function(){sendData($(this).data("topic"),"follow","topic-header-container-details-labelFollow","topic-header-container-details-labelFollowing")}),$("#pin").on("click",function(){sendData($(this).data("topic"),"pin","topic-header-container-details-labelPin","topic-header-container-details-labelPinned")});